        vm.toggle = toggle;
        vm.exists = exists;

        /**
         * Store the "settings account" in a separate variable, and not in the shared "account" variable.
         */
        var copyAccount = function (account) {
            return {
                activated: account.activated,
                email: account.email,
                firstName: account.firstName,
                langKey: account.langKey,
                lastName: account.lastName,
                login: account.login
            };
        };

        Principal.identity().then(function (account) {
            vm.settingsAccount = copyAccount(account);
        });

        function upload(file) {
        };

        // Check & Uncheck user skill
        function toggle(item, list, isSkill) {
            var idx = list.indexOf(item);
            if (idx > -1) {
                list.splice(idx, 1);
            }
            else {
                list.push(item);
            }
            if (isSkill) {
                vm.user.skills = list.toString();
            }
            else {
                vm.user.workArea = list.toString();
            }
        };

        function exists(item, list) {
            return list.indexOf(item) > -1;
        };


        function save() {
            UserDetail.update(vm.user, onSaveSuccess, onSaveError);
        }

        function onSaveSuccess(result) {
            $scope.$emit('hackathonApp:challengeUpdate', result);
            vm.alerts.push({ msg: 'Your infomation is saved!', type: "success" });
        }

        function onSaveError() {
            vm.alerts.push({ msg: 'Oops! Something went wrong!', type: "danger" });
        }

        function closeAlert(index) {
            vm.alerts.splice(index, 1);
        };
